mapper.insert:
INSERT INTO visit (
	v_date, r_no, u_no
) VALUES (
	NOW(),#{r_no},#{u_no}
)

mapper.select:
SELECT count(*) from( select * from( SELECT * , (select max(v_date) from visit where restaurant.r_no=visit.r_no)as v_date FROM shop.restaurant where r_no in (select r_no from visit where u_no=#{u_no} group by r_no)
and (r_name LIKE '%${searchWord}%' OR r_foodtype LIKE '%${searchWord}%')
and ${searchType} LIKE '%${searchWord}%'
)t
and v_date >= date_add(now(), interval -1 month)
and v_date >= date_add(now(), interval -2 month)
and v_date >= date_add(now(), interval -3 month)
)t order by v_date desc;
